export const cartReducer = (state, action) => {
  const { type, payload } = action;

  const addToCart = (product) => {
    const updatedCart = state.cartList.concat(product);
  };

  switch (type) {
    case "ADD_TO_CART":

    case "REMOVE_FROM_CART":

    case "UPDATE_TOTAL":

    default:
      throw new Error("No Case Found In cartReducer");
  }
};
